import { Component, inject, signal } from '@angular/core';
import { CommonModule } from '@angular/common';
import emailjs from '@emailjs/browser';
import { ToastrService } from 'ngx-toastr';

@Component({
  selector: 'app-contact',
  standalone: true,
  imports: [CommonModule],
  template: `
    <form class="space-y-4 md:space-y-6" (submit)="sendEmail($event)">
      
      <div class="grid md:grid-cols-2 gap-4 md:gap-6">
        <input type="text" name="user_name" required placeholder="Name"
          class="w-full bg-slate-50 dark:bg-white/5 border border-slate-200 dark:border-white/10 rounded-2xl px-6 py-4 text-slate-900 dark:text-white focus:border-brand-accent outline-none transition-colors placeholder:text-slate-400 text-sm font-medium">

        <input type="email" name="user_email" required placeholder="Email"
          class="w-full bg-slate-50 dark:bg-white/5 border border-slate-200 dark:border-white/10 rounded-2xl px-6 py-4 text-slate-900 dark:text-white focus:border-brand-accent outline-none transition-colors placeholder:text-slate-400 text-sm font-medium">
      </div>

      <input type="text" name="subject" required placeholder="Subject"
        class="w-full bg-slate-50 dark:bg-white/5 border border-slate-200 dark:border-white/10 rounded-2xl px-6 py-4 text-slate-900 dark:text-white focus:border-brand-accent outline-none transition-colors placeholder:text-slate-400 text-sm font-medium">

      <textarea name="message" required rows="4" placeholder="Your Message"
        class="w-full bg-slate-50 dark:bg-white/5 border border-slate-200 dark:border-white/10 rounded-2xl px-6 py-4 text-slate-900 dark:text-white focus:border-brand-accent outline-none transition-colors resize-none placeholder:text-slate-400 text-sm font-medium"></textarea>

      <button type="submit" [disabled]="isLoading()"
        class="w-full py-4 md:py-5 bg-brand-accent text-slate-950 font-black uppercase tracking-widest text-[10px] md:text-xs rounded-2xl flex items-center justify-center gap-2 group disabled:opacity-70 disabled:cursor-not-allowed transition-all">
        <ng-container *ngIf="!isLoading()">
          INITIATE SYSTEM SYNC
        </ng-container>
        <ng-container *ngIf="isLoading()">
          <svg class="animate-spin h-4 w-4 text-slate-950" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
            <circle class="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" stroke-width="4"></circle>
            <path class="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
          </svg>
          SYNCING...
        </ng-container>
      </button>

    </form>
  `
})
export class ContactComponent {
  private toastr = inject(ToastrService);
  isLoading = signal(false);

  sendEmail(e: Event) {
    e.preventDefault();
    this.isLoading.set(true);

    emailjs.sendForm(
      'service_44jhqx5',
      'template_1npdvdg',
      e.target as HTMLFormElement,
      'OBqWSu5kM1y_T4803'
    ).then(
      () => {
        this.toastr.success('Message sent successfully', 'SUCCESS üöÄ');
        (e.target as HTMLFormElement).reset(); // reset form
        this.isLoading.set(false);
      },
      (error) => {
        console.error(error);
        this.toastr.error('System synchronization failed', 'ERROR ‚ùå');
        this.isLoading.set(false);
      }
    );
  }
}
