import { Injectable, signal, computed, resource } from '@angular/core';

export interface Project {
    id: string;
    title: string;
    description: string;
    imageUrl: string;
    liveUrl: string;
    codeUrl: string;
    featured: boolean;
    tags: string[];
}

@Injectable({ providedIn: 'root' })
export class PortfolioStore {
    // Application State
    private _theme = signal<'dark' | 'light'>('dark');
    theme = this._theme.asReadonly();

    // Mock Data Resource
    projectsResource = resource({
        loader: async () => {
            // Simulate API delay
            await new Promise(resolve => setTimeout(resolve, 800));
            return [
                {
                    id: '1',
                    title: 'Enterprise Booking System',
                    description: 'A high-performance Ionic Angular application for global hotel chains.',
                    imageUrl: 'assets/projects/booking.jpg',
                    liveUrl: '#',
                    codeUrl: '#',
                    featured: true,
                    tags: ['Angular', 'Ionic', 'Signals']
                },
                {
                    id: '2',
                    title: 'AI Asset Manager',
                    description: '3D interactive dashboard for managing digital assets with real-time sync.',
                    imageUrl: 'assets/projects/assets.jpg',
                    liveUrl: '#',
                    codeUrl: '#',
                    featured: true,
                    tags: ['Three.js', 'RxJS', 'Angular']
                }
            ] as Project[];
        }
    });

    // Derived State
    featuredProjects = computed(() =>
        this.projectsResource.value()?.filter((p: Project) => p.featured) ?? []
    );

    isLoading = computed(() => this.projectsResource.isLoading());

    updateTheme(newTheme: 'dark' | 'light') {
        this._theme.set(newTheme);
        document.documentElement.className = newTheme;
    }
}
