import { ChangeDetectionStrategy, Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RevealDirective } from '../../shared/directives/reveal.directive';

@Component({
  selector: 'app-tech-stack',
  standalone: true,
  changeDetection: ChangeDetectionStrategy.OnPush,
  imports: [CommonModule, RevealDirective],
  templateUrl: './tech-stack.component.html'
})
export class TechStackComponent {
  techGroups = [
    { name: 'Angular (12 - 19)', level: 'Expert', percentage: 98 },
    { name: 'Ionic & Capacitor', level: 'Expert', percentage: 95 },
    { name: 'TypeScript / RxJS', level: 'Advanced', percentage: 92 },
    { name: 'Node.js / Express', level: 'Intermediate', percentage: 75 },
    { name: 'Tailwind CSS', level: 'Core', percentage: 90 },
    { name: 'Three.js / WebGL', level: 'Specialist', percentage: 65 }
  ];
}
