import { ChangeDetectionStrategy, Component, ElementRef, OnInit, ViewChild, inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { ThreeService } from '../../core/api/three.service';
import { RevealDirective } from '../../shared/directives/reveal.directive';

@Component({
    selector: 'app-hero',
    standalone: true,
    changeDetection: ChangeDetectionStrategy.OnPush,
    imports: [CommonModule, RouterModule, RevealDirective],
    templateUrl: './hero.component.html'
})
export class HeroComponent implements OnInit {
    @ViewChild('canvasContainer', { static: true }) canvasContainer!: ElementRef;
    private threeService = inject(ThreeService);

    ngOnInit() {
        this.threeService.initHeroScene(this.canvasContainer.nativeElement);
    }
}
