import { Component, Input } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Project } from '../../../core/store/portfolio.store';

@Component({
  selector: 'app-project-card',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div class="group relative bg-slate-900/40 border border-white/5 rounded-3xl overflow-hidden hover:border-brand-accent/30 transition-all duration-500">
      <!-- Media Section -->
      <div class="relative aspect-video overflow-hidden bg-slate-950">
        @if (isIonic) {
          <!-- Mobile Device Mockup -->
          <div class="absolute inset-0 flex items-center justify-center p-4">
             <div class="relative w-32 h-[260px] bg-slate-800 rounded-[3rem] border-[4px] border-slate-700 shadow-2xl overflow-hidden group-hover:scale-105 transition-transform duration-700">
                <div class="absolute top-0 left-1/2 -translate-x-1/2 w-16 h-4 bg-slate-700 rounded-b-xl z-10"></div>
                <img [src]="project.imageUrl" class="w-full h-full object-cover">
             </div>
          </div>
        } @else {
          <!-- Standard Screenshot -->
          <img [src]="project.imageUrl" class="w-full h-full object-cover group-hover:scale-110 transition-transform duration-700">
        }
        <div class="absolute inset-0 bg-gradient-to-t from-slate-950 via-transparent to-transparent opacity-80"></div>
      </div>

      <!-- Content Section -->
      <div class="p-8">
        <div class="flex flex-wrap gap-2 mb-4">
          @for (tag of project.tags; track tag) {
            <span class="text-[10px] font-bold uppercase tracking-wider px-2 py-0.5 rounded bg-brand-accent/10 text-brand-accent border border-brand-accent/20">
              {{ tag }}
            </span>
          }
        </div>
        <h3 class="text-2xl font-display font-bold mb-3 group-hover:text-brand-accent transition-colors">{{ project.title }}</h3>
        <p class="text-slate-400 text-sm leading-relaxed mb-8 line-clamp-2">
          {{ project.description }}
        </p>

        <div class="flex items-center gap-4">
          <a [href]="project.liveUrl" target="_blank" class="flex-1 text-center py-3 bg-brand-accent text-slate-950 text-sm font-bold rounded-xl hover:shadow-[0_0_20px_rgba(0,242,255,0.2)] transition-all">
            VIEW LIVE
          </a>
          <a [href]="project.codeUrl" target="_blank" class="px-5 py-3 bg-white/5 border border-white/10 text-white text-sm font-bold rounded-xl hover:bg-white/10 transition-all">
            CODE
          </a>
        </div>
      </div>
    </div>
  `
})
export class ProjectCardComponent {
  @Input({ required: true }) project!: Project;

  get isIonic(): boolean {
    return this.project.tags.includes('Ionic');
  }
}
