import { ChangeDetectionStrategy, Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RevealDirective } from '../../shared/directives/reveal.directive';

@Component({
  selector: 'app-experience-timeline',
  standalone: true,
  changeDetection: ChangeDetectionStrategy.OnPush,
  imports: [CommonModule, RevealDirective],
  templateUrl: './experience-timeline.component.html'
})
export class ExperienceTimelineComponent {
  experiences = [
    {
      period: '2022 - Present',
      role: 'Senior Frontend Architect',
      company: 'TechGlobal Solutions',
      responsibilities: [
        'Architected and led the migration of a legacy retail banking platform to **Angular 19 Standalone** architecture, improving bundle size by 40%.',
        'Defined core architectural patterns using **Signals** and the **Resource API** to eliminate unnecessary change detection cycles.',
        'Engineered a cross-platform mobile suite using **Ionic & Capacitor**, achieving 95% code sharing across iOS, Android, and Web.',
        'Established a company-wide Design System based on Tailwind CSS, reducing UI development time by 30% for internal teams.'
      ]
    },
    {
      period: '2019 - 2022',
      role: 'Lead Angular Developer',
      company: 'AppInnovate',
      responsibilities: [
        'Developed high-performance hotel booking engines processing over **$2M in daily transactions**.',
        'Implemented complex real-time data orchestration using **RxJS** for live flight tracking and pricing updates.',
        'Optimized LCP and TBT scores to consistently achieve **Lighthouse 95+** for client-facing B2C portals.',
        'Mentored a team of 10 developers on modern Angular best practices and state management strategies.'
      ]
    },
    {
      period: '2017 - 2019',
      role: 'Full Stack Developer',
      company: 'StartUp Hub',
      responsibilities: [
        'Built and launched 12+ MVP products for seed-stage startups using the **Angular & Node.js** stack.',
        'Designed and implemented micro-frontend architectures to support multiple vertical-specific dashboards.',
        'Configured automated CI/CD pipelines with GitHub Actions, reducing deployment time from hours to minutes.',
        'Delivered pixel-perfect, responsive UI/UX from high-fidelity Figma designs.'
      ]
    }
  ];
}
