import { ChangeDetectionStrategy, Component, inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { PortfolioStore } from '../../core/store/portfolio.store';

@Component({
    selector: 'app-navbar',
    standalone: true,
    imports: [CommonModule],
    template: `
    <nav class="fixed top-0 w-full z-50 bg-slate-950/80 backdrop-blur-md border-b border-white/10">
      <div class="max-w-7xl mx-auto px-6 h-20 flex items-center justify-between">
        <div class="flex items-center gap-2">
          <div class="w-10 h-10 bg-brand-accent rounded-lg flex items-center justify-center font-bold text-slate-950 text-xl">
            R
          </div>
          <span class="font-display text-xl font-bold tracking-tight">RAZA<span class="text-brand-accent">.</span>DEP</span>
        </div>

        <div class="hidden md:flex items-center gap-8 text-sm font-medium text-slate-400">
          <a href="#about" class="hover:text-brand-accent transition-colors">About</a>
          <a href="#projects" class="hover:text-brand-accent transition-colors">Projects</a>
          <a href="#experience" class="hover:text-brand-accent transition-colors">Experience</a>
          <a href="#contact" class="px-5 py-2.5 bg-brand-accent text-slate-950 rounded-full font-bold hover:scale-105 transition-transform active:scale-95">Let's Talk</a>
        </div>

        <button (click)="store.updateTheme(store.theme() === 'dark' ? 'light' : 'dark')" class="p-2 hover:bg-white/5 rounded-full transition-colors">
          @if (store.theme() === 'dark') {
            <svg xmlns="http://www.w3.org/2000/svg" class="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M12 3v1m0 16v1m9-9h-1M4 12H3m15.364-6.364l-.707.707M6.343 17.657l-.707.707m12.728 0l-.707-.707M6.343 6.343l-.707-.707M12 5a7 7 0 100 14 7 7 0 000-14z" />
            </svg>
          } @else {
            <svg xmlns="http://www.w3.org/2000/svg" class="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M20.354 15.354A9 9 0 018.646 3.646 9.003 9.003 0 0012 21a9.003 9.003 0 008.354-5.646z" />
            </svg>
          }
        </button>
      </div>
    </nav>
  `
})
export class NavbarComponent {
    store = inject(PortfolioStore);
}
