import { Component, inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { PortfolioStore } from '../../core/store/portfolio.store';
import { ProjectCardComponent } from '../../shared/components/project-card/project-card.component';

@Component({
    selector: 'app-projects-gallery',
    standalone: true,
    imports: [CommonModule, ProjectCardComponent],
    template: `
    <section id="projects" class="py-24 bg-slate-950">
      <div class="max-w-7xl mx-auto px-6">
        <div class="flex flex-col md:flex-row md:items-end justify-between gap-8 mb-16">
          <div class="max-w-2xl">
            <h2 class="text-brand-accent font-bold uppercase tracking-widest text-sm mb-4">Selected Works</h2>
            <h3 class="text-4xl md:text-6xl font-display font-bold leading-tight">
              Crafting high-performance <span class="text-slate-500 italic">digital architectures.</span>
            </h3>
          </div>
          <div class="flex gap-4">
            <!-- Filter logic would go here, using Signals -->
            <button class="px-6 py-2 rounded-full border border-white/10 text-sm font-medium hover:bg-white/5 transition-colors">All</button>
            <button class="px-6 py-2 rounded-full border border-brand-accent/30 bg-brand-accent/10 text-brand-accent text-sm font-medium">Ionic</button>
            <button class="px-6 py-2 rounded-full border border-white/10 text-sm font-medium hover:bg-white/5 transition-colors">Angular</button>
          </div>
        </div>

        @if (store.isLoading()) {
          <div class="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
            @for (i of [1,2,3]; track i) {
              <div class="h-[400px] rounded-3xl bg-slate-900 animate-pulse"></div>
            }
          </div>
        } @else {
          <div class="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
            @for (project of store.projectsResource.value(); track project.id) {
              <app-project-card [project]="project"></app-project-card>
            }
          </div>
        }
      </div>
    </section>
  `
})
export class ProjectsGalleryComponent {
    store = inject(PortfolioStore);
}
